
DROP TABLE IF EXISTS "covid_data" CASCADE;

CREATE TABLE covid_data(
pkid INTEGER PRIMARY KEY NOT NULL,
country VARCHAR (254),
confirmed NUMERIC (24, 15),
deaths NUMERIC (24, 15),
mortality NUMERIC (24, 15),
latitude NUMERIC,
longitude NUMERIC,
country_code VARCHAR(254)
);


SELECT * FROM covid_data

-- Creating and updating the geom column
ALTER TABLE covid_data
ADD COLUMN location geometry (Point ,4326) ;

UPDATE covid_data
SET location = st_setSRID(st_makePoint(latitude,longitude), 4326);

